import React, { useState, useEffect } from 'react';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalUsers: 0,
    activeAccounts: 0,
    totalBalance: 0,
    pendingRequests: 0
  });
  const [recentUsers, setRecentUsers] = useState([]);
  const [recentTransactions, setRecentTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_URL = 'https://localhost:443'; // Your HTTPS server

  // Fetch all data on component mount
  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      // Fetch users
      const usersResponse = await fetch(`${API_URL}/admin/users`);
      const usersData = await usersResponse.json();
      
      // Fetch transactions
      const transactionsResponse = await fetch(`${API_URL}/admin/transactions`);
      const transactionsData = await transactionsResponse.json();

      // Calculate stats
      const totalUsers = usersData.length || 0;
      const activeUsers = usersData.filter(user => user.role !== 'inactive').length || 0;
      const totalBalance = usersData.reduce((sum, user) => sum + (parseFloat(user.balance) || 0), 0);
      const pendingRequests = transactionsData.filter(txn => txn.status === 'pending').length || 0;

      setStats({
        totalUsers,
        activeAccounts: activeUsers,
        totalBalance: `$${totalBalance.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`,
        pendingRequests
      });

      // Set recent users (last 5)
      setRecentUsers(usersData.slice(0, 5));

      // Set recent transactions (last 5)
      setRecentTransactions(transactionsData.slice(0, 5));

      setLoading(false);
    } catch (err) {
      console.error('Error fetching dashboard data:', err);
      setError('Failed to load dashboard data. Make sure the server is running.');
      setLoading(false);
    }
  };

  const renderIcon = (iconType) => {
    switch(iconType) {
      case 'users':
        return (
          <svg viewBox="0 0 24 24" strokeWidth="2">
            <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2" />
            <circle cx="9" cy="7" r="4" />
            <path d="M23 21v-2a4 4 0 0 0-3-3.87" />
            <path d="M16 3.13a4 4 0 0 1 0 7.75" />
          </svg>
        );
      case 'account':
        return (
          <svg viewBox="0 0 24 24" strokeWidth="2">
            <rect x="2" y="7" width="20" height="14" rx="2" ry="2" />
            <path d="M16 21V5a2 2 0 0 0-2-2h-4a2 2 0 0 0-2 2v16" />
          </svg>
        );
      case 'dollar':
        return (
          <svg viewBox="0 0 24 24" strokeWidth="2">
            <line x1="12" y1="1" x2="12" y2="23" />
            <path d="M17 5H9.5a3.5 3.5 0 0 0 0 7h5a3.5 3.5 0 0 1 0 7H6" />
          </svg>
        );
      case 'clock':
        return (
          <svg viewBox="0 0 24 24" strokeWidth="2">
            <circle cx="12" cy="12" r="10" />
            <polyline points="12 6 12 12 16 14" />
          </svg>
        );
      default:
        return null;
    }
  };

  const handleLogout = () => {
    // Clear session and redirect to login
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  const handleBack = () => {
    // Navigate back to home
    window.location.href = '/home';
  };

  const handleEdit = async (username) => {
    console.log('Edit clicked for user:', username);
    // Add your edit logic here - maybe open a modal or navigate to edit page
  };

  const handleDelete = async (username) => {
    if (window.confirm(`Are you sure you want to delete user ${username}?`)) {
      try {
        const response = await fetch(`${API_URL}/admin/users/${username}`, {
          method: 'DELETE'
        });
        
        if (response.ok) {
          fetchDashboardData();
          alert('User deleted successfully');
        } else {
          alert('Failed to delete user');
        }
      } catch (err) {
        console.error('Error deleting user:', err);
        alert('Error deleting user');
      }
    }
  };

  const handleAddUser = () => {
    // Navigate to signup or open modal
    window.location.href = '/register';
  };

  const handleVerifyTransaction = async (txnId) => {
    try {
      const response = await fetch(`${API_URL}/admin/verify`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ txnId })
      });

      if (response.ok) {
        fetchDashboardData();
        alert('Transaction approved successfully');
      } else {
        alert('Failed to approve transaction');
      }
    } catch (err) {
      console.error('Error verifying transaction:', err);
      alert('Error verifying transaction');
    }
  };

  const handleDenyTransaction = async (txnId) => {
    if (window.confirm('Are you sure you want to deny this transaction?')) {
      try {
        const response = await fetch(`${API_URL}/admin/deny`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ txnId })
        });

        if (response.ok) {
          fetchDashboardData();
          alert('Transaction denied');
        } else {
          alert('Failed to deny transaction');
        }
      } catch (err) {
        console.error('Error denying transaction:', err);
        alert('Error denying transaction');
      }
    }
  };

  const formatDate = (timestamp) => {
    if (!timestamp) return 'N/A';
    const date = new Date(timestamp);
    return date.toLocaleDateString('en-US');
  };

  const statsData = [
    { label: 'Total Users', value: stats.totalUsers, icon: 'users' },
    { label: 'Active Accounts', value: stats.activeAccounts, icon: 'account' },
    { label: 'Total Balance', value: stats.totalBalance, icon: 'dollar' },
    { label: 'Pending Requests', value: stats.pendingRequests, icon: 'clock' }
  ];

  if (loading) {
    return (
      <div className="admin-container">
        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          height: '100vh',
          fontSize: '1.5rem',
          color: '#2d3436'
        }}>
          Loading dashboard...
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="admin-container">
        <div style={{ 
          display: 'flex', 
          flexDirection: 'column',
          justifyContent: 'center', 
          alignItems: 'center', 
          height: '100vh',
          fontSize: '1.2rem',
          color: '#d63031',
          textAlign: 'center',
          padding: '20px'
        }}>
          <p>{error}</p>
          <button 
            onClick={fetchDashboardData}
            style={{
              marginTop: '20px',
              padding: '10px 20px',
              background: 'white',
              border: '2px solid #2d3436',
              borderRadius: '8px',
              cursor: 'pointer',
              fontWeight: '500'
            }}
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-container">
      <nav className="admin-navbar">
        <h1 className="admin-nav-title">Admin Dashboard</h1>
        <div className="admin-nav-links">
          <button onClick={handleBack}>Back to Home</button>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      <div className="admin-content">
        {/* Stats Section */}
        <div className="stats-grid">
          {statsData.map((stat, index) => (
            <div key={index} className="stat-card">
              <div className="stat-header">
                <div className="stat-icon">
                  {renderIcon(stat.icon)}
                </div>
                <span className="stat-label">{stat.label}</span>
              </div>
              <div className="stat-value">{stat.value}</div>
            </div>
          ))}
        </div>

        {/* Recent Users Section */}
        <div className="section-header">
          <h2 className="section-title">Recent Users</h2>
          <div className="section-actions">
            <button onClick={handleAddUser}>+ Add User</button>
          </div>
        </div>

        <div className="table-container">
          <table className="admin-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Username</th>
                <th>Account Number</th>
                <th>Balance</th>
                <th>Role</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {recentUsers.length > 0 ? (
                recentUsers.map((user) => (
                  <tr key={user.username}>
                    <td>{user.fullName || 'N/A'}</td>
                    <td>{user.username || 'N/A'}</td>
                    <td>{user.accountNumber || 'N/A'}</td>
                    <td>${parseFloat(user.balance || 0).toFixed(2)}</td>
                    <td>
                      <span className={`status-badge status-${user.role === 'admin' ? 'active' : 'pending'}`}>
                        {(user.role || 'user').toUpperCase()}
                      </span>
                    </td>
                    <td>
                      <div className="action-buttons">
                        <button 
                          className="action-btn action-btn-edit"
                          onClick={() => handleEdit(user.username)}
                        >
                          Edit
                        </button>
                        <button 
                          className="action-btn action-btn-delete"
                          onClick={() => handleDelete(user.username)}
                        >
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" style={{ textAlign: 'center', padding: '40px' }}>
                    No users found
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        {/* Recent Transactions Section */}
        <div className="section-header">
          <h2 className="section-title">Recent Transactions</h2>
        </div>

        <div className="table-container">
          <table className="admin-table">
            <thead>
              <tr>
                <th>Account</th>
                <th>Amount</th>
                <th>Currency</th>
                <th>SWIFT</th>
                <th>Status</th>
                <th>Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {recentTransactions.length > 0 ? (
                recentTransactions.map((transaction) => (
                  <tr key={transaction.id}>
                    <td>{transaction.customer || 'N/A'}</td>
                    <td>${parseFloat(transaction.amount || 0).toFixed(2)}</td>
                    <td>{transaction.currency || 'N/A'}</td>
                    <td>{transaction.swiftCode || 'N/A'}</td>
                    <td>
                      <span className={`status-badge status-${
                        transaction.status === 'approved' ? 'active' : 
                        transaction.status === 'denied' ? 'inactive' : 'pending'
                      }`}>
                        {(transaction.status || 'pending').toUpperCase()}
                      </span>
                    </td>
                    <td>{formatDate(transaction.createdAt)}</td>
                    <td>
                      <div className="action-buttons">
                        {transaction.status === 'pending' && (
                          <>
                            <button 
                              className="action-btn action-btn-edit"
                              onClick={() => handleVerifyTransaction(transaction.id)}
                            >
                              Approve
                            </button>
                            <button 
                              className="action-btn action-btn-delete"
                              onClick={() => handleDenyTransaction(transaction.id)}
                            >
                              Deny
                            </button>
                          </>
                        )}
                        {transaction.status !== 'pending' && (
                          <button 
                            className="action-btn"
                            onClick={() => console.log('View details:', transaction.id)}
                          >
                            View
                          </button>
                        )}
                      </div>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="7" style={{ textAlign: 'center', padding: '40px' }}>
                    No transactions found
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
